INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKIF16MHT26', 2, CAST(299.9900 AS Money), N'The Infinity Crazyhorse HT26 Backroads 8 unisex MTB with an aluminum frame is all-purpose and versatile for all of your on and off road adventures.  It has Shimano 21-speed Shimano EF-41 Easyfire trigger shifters and Shimano 7-speed rear derailleur with Shimano 3-speed front derailleur alloy V-brakes and alloy rims. Front suspension provides a smoother riding on the trails.', N'CRAZYHORSE HT26', CAST(269.9900 AS Money), N'Infinity Backroads', 5, 5)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKIF16UBOT18', 2, CAST(279.9900 AS Money), N'The Infinity Boss.two unisex hybrid bike has an aluminium 6061 frame with rigid straight blade front fork.  Comfort and performance are built into each frame, offering around-town riding opportunities.', N'Boss.two', CAST(259.9900 AS Money), N'Infinity Boss', 5, 7)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKIH16MDESP29', 4, CAST(379.9900 AS Money), N'The Ironhorse Desperado is a classic aluminum hardtail mountain bike with a front suspension fork. It is really fun to ride on rougher terrain or just on some back pavement on the way to work. The Desperado features large 29-in. wheels that will make you feel like you can roll over anything. There are 21 speeds with a Shimano EF51 EZ-Fire shifter and rear derailleur for lots of gear options and great smooth shifting.', N'Desperado', CAST(350.9900 AS Money), N'Ironhorse Men’s Bike', 10, 3)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKIH16MRDR18', 4, CAST(379.9900 AS Money), N'If you like the styling of a mountain bike but in reality you will be riding more for commuting and everyday fitness, then look at a hybrid or multi-use bike as a solution. The mechanical disc brakes let you stop smoothly in damp or dusty conditions and the alloy crank and rims, typically seen on mountain bikes keep the bike lightweight and fun to ride. ', N'Raider 700c Hybrid', CAST(369.9900 AS Money), N'Ironhorse Men’s Bike', 20, 15)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKIH16MTEMP18', 4, CAST(269.9900 AS Money), N'This IronHorse men’s Tempo is the perfect bike for riders who crave a little versatility – you''ll be comfortable during your commute, riding for fitness, or just out for a ride with the family. The Tempo features an upright riding position with sweptback handlebars, spring seat, and a steel hybrid frame with a front suspension fork and suspension seat post for added comfort. ', N'Tempo 700c', CAST(259.9900 AS Money), N'Ironhorse Men’s Bike', 5, 3)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKKN16LRCK165', 9, CAST(229.9900 AS Money), N'The 21-speed Rock Mountain dual suspension mountain bike is ready to tackle any trail.  Built around a hand-crafted steel frame and a suspension fork, this bicycle is designed to handle big hits and come back for more.  Alloy rims and a Shimano rear derailleur round out this powerful package.', N'Rock Mountain', CAST(219.9900 AS Money), N'Kent Ladies Bike', 5, 1)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKKN16LTRA26', 9, CAST(299.9900 AS Money), N'The Terra mountain bike is ready to tackle any trail any time.  It starts with a hand crafted full suspension aluminium frame that’s built strong yet light enough for great handling and all day epic rides.  A simple and reliable single pivot design keeps trips to the bike shop at a minimum.', N'Terra Mountain', CAST(279.9900 AS Money), N'Kent Ladies Bike', 5, 2)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKKN16MLAK185', 9, CAST(269.9900 AS Money), N'This comfortable riding cruiser bike has a very soft wide saddle to ensure comfort. With a front basket and a rear rack, this bike is great for carrying your laptop, groceries, small errands or just to go for a quite sightseeing cruise.', N'Lakewood Cruiser', CAST(249.9900 AS Money), N'Kent Men''s Bike', 5, 3)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKKN16MSAF185', 9, CAST(229.9900 AS Money), N'This dual suspension comfort bike is the perfect bike for anyone looking for reliability comfort and tremendous value in a city bicycle.  The heart of the Safari is a hand crafted; lightweight aluminum frame which soaks up bumps on the most uneven roads.', N'Safari', CAST(219.9900 AS Money), N'Kent Men''s Bike', 5, 2)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKKN16MTRA26', 9, CAST(299.9900 AS Money), N'The Terra mountain bike is ready to tackle any trail any time.  It starts with a hand crafted full suspension aluminium frame that’s built strong yet light enough for great handling and all day epic rides.  A simple and reliable single pivot design keeps trips to the bike shop at a minimum.', N'Terra Mountain', CAST(279.9900 AS Money), N'Kent Men''s Bike', 5, 2)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKKO16BRKD13', 3, CAST(1199.0000 AS Money), N'Clean, simple, stylish and dependable: we pull out all the stops on the Dr Dew. Its 1x drivetrain is the first thing to catch your eye—the simple wide range of gears is perfect for flat cruises but also makes quick work of fast hill sprints. The sensible fenders, hydraulic disc brakes and plush Clement 40c tires showcase our attention to urban environments. Designed to turn heads, the Dr. Dew is built to last like only a Kona can.', N'Dr Dew', CAST(1099.0000 AS Money), N'Kona Men’s Bike', 5, 7)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKKO17MRDR18', 3, CAST(2499.0000 AS Money), N'The road ahead may be paved with good intentions, but where it ends and the real adventure begins is when the Sutra LTD is at its best. The steel frame begs for long days on roads no longer found on a map, and the hydraulic brakes and 1x drivetrain provide all-around capability when those roads become trails and the thrill of exploration takes over.', N'Sutra LTD', CAST(2259.0000 AS Money), N'Kona Men’s Bike', 5, 5)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKKO17MTEMP18', 3, CAST(599.0000 AS Money), N'Built to withstand a lifetime of miles and evoke a smile every time you ride. Relaxed and confidence-inspiring, the Dew is designed with comfort in mind. We spec’d it smartly to ensure it makes quick work of commutes and leisurely Sunday rides on the local bike path.', N'Dew', CAST(599.0000 AS Money), N'Kona Men’s Bike', 20, 7)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKKO17UBOT18', 3, CAST(1799.0000 AS Money), N'Multiple Bike-of-the-Year awards speak for themselves. The Ute is a well-designed, carry-everything utility bike that makes quick work of trips to the store, dropping the kids off at school and bringing the tree home for the holidays. We didn’t need any awards to tell us how rad this bike is, but it’s nice to see that other people get it.', N'UTE', CAST(1650.0000 AS Money), N'Kona Men’s Bike', 10, 3)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKMG16BRKD11', 1, CAST(269.9900 AS Money), N'This Mongoose Rockadile is perfect for young mountain bikers getting into the sport, featuring a sturdy Mongoose youth MTB aluminum hard tail frame with a Mongoose suspension fork to soak up bumps on the trail. The Shimano Tourney 21-speed drivetrain comes with premium Tourney ez-fire Shifters for smooth shifting when climbing those pesky hills.', N'Rockadile Bike', CAST(249.9900 AS Money), N'Mongoose Boy’s Bike', 10, 5)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKMG16BRKD13', 1, CAST(299.9900 AS Money), N'This Mongoose Rockadile is perfect for young mountain bikers getting into the sport, featuring a sturdy Mongoose youth MTB aluminum hard tail frame with a Mongoose suspension fork to soak up bumps on the trail. The Shimano Tourney 21-speed drivetrain comes with premium Tourney ez-fire Shifters for smooth shifting when climbing those pesky hills.', N'Rockadile Bike', CAST(249.9900 AS Money), N'Mongoose Boy’s Bike', 5, 2)
