INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKMG16MKORE29', 1, CAST(599.9900 AS Money), N'This Mongoose Reform bike has wider tires than your typical commuter, perfect for rides with rougher roads or gravel paths. Light trails, rocky paths and long pavement rides are all in play on the Reform. Speedy like a road bike, with the durability of a mountain bike, and as comfortable as a cruiser, hybrid bikes are built for everyday riding.', N'Reform Sport 700c', CAST(549.9900 AS Money), N'Mongoose Men’s Bike', 10, 8)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKMG16MTYAX26', 1, CAST(679.9900 AS Money), N'Cross country mountain biking doesn''t need to be complicated. The Mongoose TYAX SUPA understands your riding needs; it''s here to deliver a smooth ride that''s sturdy but still has a hearty helping of agility. Extra tire clearance from plus-sized tires means better traction on steep climbs and gnarly descents. Ride faster, hug corners tighter and ride new trails with that added boost of confidence.', N'TYAX SUPA SPORT', CAST(669.9900 AS Money), N'Mongoose Men’s Hardtail Bike', 30, 5)
INSERT INTO [dbo].[Products] ([Id], [BrandId], [CostPrice], [Description], [GraphicName], [MSRP], [ProductName], [QtyOnBackOrder], [QtyOnHand]) VALUES (N'BKMG16MTYAX29', 1, CAST(589.9900 AS Money), N'The Tyax is even more fun as a 29er. You can roll over anything! Cross country mountain biking doesn''t need to be complicated. The Mongoose Tyax understands your riding needs; it''s here to deliver a smooth ride that''s sturdy but still has a hearty helping of agility', N'TYAX Hardtail', CAST(569.9900 AS Money), N'Mongoose Men’s Bike', 10, 5)
